import { useEffect, useState } from "react";
import { useTheme } from "../theme/hooks";

export function getColors(): object {
  const { colors } = useTheme();
  const [color, SetColor] = useState({});

  useEffect(() => {
    SetColor(colors);

    return () => {
      color;
    };
  }, []);

  return color;
}
