import React from "react";
import { Text, View, Image, ScrollView } from "react-native";
import "redux";

interface NavigationProps {
  navigation: any;
  route: any;
}

type Props = NavigationProps;

const renderContent = (data: any) => {

  console.log("masterDetailObj", data);
  const actors = data?.Actors.split(",");
  const genre = data?.Genre.split(",");
  return (
    <ScrollView>
    <View style={{ alignItems: "center", marginTop: 20, width: "100%" }}>
      <Image
        source={{ uri: data?.Poster }}
        style={{ height: 350, width: 250, borderRadius: 10 }}
      />
    </View>
    <View style={{ paddingLeft: 20 }}>
      <Text style={{ fontSize: 25, fontWeight: "bold", marginTop: 10 }}>
        Plot
      </Text>
      <Text style={{ paddingTop: 20, paddingRight: 10, fontSize: 18 }}>
        {data?.Plot}
      </Text>
      <Text style={{ fontSize: 25, fontWeight: "bold", marginTop: 10 }}>
        Genre
      </Text>
      <View
        style={{
          flexDirection: "row",
          width: "100%",
          flexWrap: "wrap",
          marginTop: 20,
        }}
      >
        {genre.map((genre: string, i: number) => {
          return (
            <View
              key={i}
              style={{
                flexDirection: "row",
                height: 40,
                width: "auto",
                borderColor: "#E3E3E3",
                backgroundColor: "#E3E3E3",
                borderWidth: 2,
                borderRadius: 10,
                alignItems: "center",
                justifyContent: "center",
                marginRight: 10,
                marginBottom: 10,
                padding: 5,
              }}
            >
              <Text>{genre}</Text>
            </View>
          );
        })}
      </View>
      <Text style={{ fontSize: 25, fontWeight: "bold", marginTop: 10 }}>
        Actors
      </Text>
      <View
        style={{
          flexDirection: "row",
          width: "100%",
          flexWrap: "wrap",
          marginTop: 20,
        }}
      >
        {actors.map((actor: string, i: number) => {
          return (
            <View
              key={i}
              style={{
                flexDirection: "row",
                height: 40,
                width: "auto",
                borderColor: "#E3E3E3",
                backgroundColor: "#E3E3E3",
                borderWidth: 2,
                borderRadius: 10,
                alignItems: "center",
                justifyContent: "center",
                marginRight: 10,
                marginBottom: 10,
                padding: 5,
              }}
            >
              <Text>{actor}</Text>
            </View>
          );
        })}
      </View>
      <Text style={{ marginBottom: 50 }}>{data?.Year}</Text>
    </View>
  </ScrollView>
  )
}

class MasterDetail extends React.Component<Props> {
  render() {
    const { masterDetailObj } = this.props.route.params;

    return (
      <>
       {renderContent(masterDetailObj)}
       </>
    );
  }
}

export default MasterDetail;
