import React from "react";
import { StyleSheet, Text, View, Image, Button, ScrollView } from "react-native";
import { TextInput } from '../../components'
import { connect } from 'react-redux';
import 'redux';
import { RootState, Dispatch } from '../../store'

const mapState = (state: RootState) => ({
    searchText: state.search.searchText,
    searchData: state.search.searchData
})

const mapDispatch = (dispatch: Dispatch) => ({
    setSearchText: (searchText: string) => dispatch.search.searchText(searchText),
    searchAsync: (searchText: string) => dispatch.search.searchAsync(searchText)
})

type StateProps = ReturnType<typeof mapState>
type DispatchProps = ReturnType<typeof mapDispatch>
type Props = StateProps & DispatchProps

class Series extends React.Component<Props> {

    render() {
        const seriesData = this.props.searchData
        const renderSeriesContent = () => {
            return seriesData.Search && seriesData.Search.map && seriesData.Search.map((item: { Title: string; Poster: string; }, i: number) => {
                return (
                    <View key={i} style={{ flexDirection: "row", width: '100%', paddingLeft: 15, paddingTop: 5, marginBottom: 10 }}>
                        {item && item.Poster ? <Image source={{ uri: item && item.Poster }} style={{ height: 200, width: 150, borderRadius: 10 }} /> : <Text>No image</Text>}

                        <Text style={{ marginLeft: 15 }} key={i}>{item && item.Title}</Text>
                    </View>
                )
            })
        }

        return (
            <>
                <View style={{
                    flex: 1, flexDirection: 'row', alignItems: "center", width: "100%"
                }}>
                    <TextInput
                        placeholder="Search series"
                        onChangeText={(input: string) => { this.props.setSearchText(input) }}
                        backgroundColor="#E3E3E3"
                        color="black"
                        borderColor="gray"
                        fontSize={16}
                        padding={10}
                        marginLeft={10}
                        marginBottom={5}
                        marginRight={10}
                    />
                    <Button color="black" title="Search" onPress={() => this.props.searchAsync(this.props.searchText)} />
                </View>
                <ScrollView style={{
                    flex: 45, width: '100%'
                }}>
                    {seriesData.Search ?
                        renderSeriesContent() :
                        <View style={{ alignItems: "center", justifyContent: "center" }}>
                            <Image source={require("../../assets/series.png")} style={styles.logo} />
                            <Text style={{ fontSize: 18 }}>Find your favourite series.</Text>
                        </View>
                    }
                </ScrollView>

            </>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        alignItems: "center",
        marginLeft: 10,
        marginTop: 10,
        marginRight: 10
    },
    logo: {
        width: 300,
        height: 300,
        marginBottom: 5
    }
})


export default connect(mapState, mapDispatch)(Series)
